// File: client/src/pages/OrderDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../utils/api';

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState('');
  const [deliverable, setDeliverable] = useState({ description: '', attachments: [] });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const response = await api.get(`/orders/${id}`);
        setOrder(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load order');
      }
    };

    fetchOrder();
  }, [id]);

  const updateStatus = async (newStatus) => {
    try {
      const response = await api.patch(`/orders/${id}/status`, { status: newStatus });
      setOrder(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update status');
    }
  };

  const submitDeliverable = async (e) => {
    e.preventDefault();
    try {
      await api.post(`/orders/${id}/deliver`, deliverable);
      alert('Work submitted successfully!');
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit deliverable');
    }
  };

  if (error) return <p className="text-red-500 text-center">{error}</p>;
  if (!order) return <p>Loading...</p>;

  return (
    <div className="max-w-3xl mx-auto p-8">
      <h1 className="text-3xl font-bold">{order.title}</h1>
      <p className="text-gray-700 mt-4">{order.description}</p>
      <p className="text-gray-900 font-semibold mt-2">Status: {order.status}</p>
      <p className="text-gray-700 mt-2">Price: â‚¹{order.price}</p>
      <p className="text-gray-700 mt-2">Deadline: {new Date(order.deadline).toDateString()}</p>

      {order.status === 'in_progress' && (
        <form onSubmit={submitDeliverable} className="mt-6">
          <textarea
            placeholder="Describe your work"
            value={deliverable.description}
            onChange={(e) => setDeliverable({ ...deliverable, description: e.target.value })}
            required
          />
          <input
            type="file"
            multiple
            onChange={(e) => setDeliverable({ ...deliverable, attachments: Array.from(e.target.files) })}
          />
          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
            Submit Work
          </button>
        </form>
      )}

      {order.status === 'pending' && (
        <button onClick={() => updateStatus('in_progress')} className="bg-blue-500 text-white px-4 py-2 rounded">
          Start Work
        </button>
      )}

      {order.status === 'under_review' && (
        <button onClick={() => updateStatus('completed')} className="bg-green-500 text-white px-4 py-2 rounded">
          Mark as Completed
        </button>
      )}
    </div>
  );
};

export default OrderDetails;
