import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const CreateJob = () => {
  const [formData, setFormData] = useState({
    title: '', description: '', category: '', minBudget: '', maxBudget: '',
    deadline: '', skills: '', attachments: []
  });

  const navigate = useNavigate();
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleFileChange = (e) => setFormData({ ...formData, attachments: Array.from(e.target.files) });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const jobData = { ...formData, skills: formData.skills.split(',').map(skill => skill.trim()) };
      const response = await api.post('/jobs', jobData);
      navigate(`/jobs/${response.data._id}`);
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to create job');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="title" placeholder="Title" onChange={handleChange} required />
      <textarea name="description" placeholder="Description" onChange={handleChange} required />
      <input name="category" placeholder="Category" onChange={handleChange} required />
      <input name="minBudget" type="number" placeholder="Min Budget" onChange={handleChange} required />
      <input name="maxBudget" type="number" placeholder="Max Budget" onChange={handleChange} required />
      <input name="deadline" type="date" onChange={handleChange} required />
      <input name="skills" placeholder="Skills (comma-separated)" onChange={handleChange} />
      <input type="file" multiple onChange={handleFileChange} />
      <button type="submit">Create Job</button>
    </form>
  );
};

export default CreateJob;
