// File: client/src/pages/CreateJob.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const CreateJob = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    subCategory: '',
    budgetMin: '',
    budgetMax: '',
    deadline: '',
    skills: '',
    attachments: []
  });

  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      attachments: Array.from(e.target.files)
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const jobData = {
        ...formData,
        budget: { min: formData.budgetMin, max: formData.budgetMax },
        skills: formData.skills.split(',').map(skill => skill.trim())
      };

      const response = await api.post('/jobs', jobData);
      navigate(`/jobs/${response.data._id}`);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create job');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10 bg-white p-8 border border-gray-300 rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-6 text-center">Post a New Job</h1>

      {error && <p className="text-red-500 text-center mb-4">{error}</p>}

      <form onSubmit={handleSubmit}>
        <input name="title" type="text" placeholder="Title" value={formData.title} onChange={handleChange} required />
        <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
        <input name="category" type="text" placeholder="Category" value={formData.category} onChange={handleChange} required />
        <input name="subCategory" type="text" placeholder="Subcategory" value={formData.subCategory} onChange={handleChange} />
        <input name="budgetMin" type="number" placeholder="Min Budget" value={formData.budgetMin} onChange={handleChange} required />
        <input name="budgetMax" type="number" placeholder="Max Budget" value={formData.budgetMax} onChange={handleChange} required />
        <input name="deadline" type="date" value={formData.deadline} onChange={handleChange} required />
        <input name="skills" type="text" placeholder="Required Skills (comma-separated)" value={formData.skills} onChange={handleChange} />
        <input type="file" multiple onChange={handleFileChange} />

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Posting...' : 'Post Job'}
        </button>
      </form>
    </div>
  );
};

export default CreateJob;
