// File: client/src/components/Navbar.js
import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { FiMenu, FiX } from 'react-icons/fi';

const Navbar = () => {
  const { currentUser } = useContext(AuthContext);
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="bg-white shadow-md fixed w-full z-50">
      <div className="container mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-gray-900">
          BookMyService
        </Link>

        {/* Menu Button for Mobile */}
        <button
          className="md:hidden text-gray-800 text-2xl"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? <FiX /> : <FiMenu />}
        </button>

        {/* Navigation Links */}
        <ul
          className={`md:flex md:items-center md:space-x-6 absolute md:static bg-white w-full md:w-auto top-16 left-0 shadow-md md:shadow-none p-6 md:p-0 transition-all duration-300 ease-in-out ${menuOpen ? 'block' : 'hidden'}`}
        >
          <li>
            <Link to="/gigs" className="text-gray-700 hover:text-blue-500 font-medium">
              Find Gigs
            </Link>
          </li>
          <li>
            <Link to="/jobs" className="text-gray-700 hover:text-blue-500 font-medium">
              Find Jobs
            </Link>
          </li>
          {currentUser ? (
            <li>
              <Link to="/dashboard" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                Dashboard
              </Link>
            </li>
          ) : (
            <li>
              <Link to="/login" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                Sign In
              </Link>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
