// File: client/src/pages/GigDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../utils/api';

const GigDetails = () => {
  const { id } = useParams();
  const [gig, setGig] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchGig = async () => {
      try {
        const response = await api.get(`/gigs/${id}`);
        setGig(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load gig');
      }
    };

    fetchGig();
  }, [id]);

  const handleOrder = async () => {
    try {
      await api.post(`/orders/from-gig/${id}`);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to place order');
    }
  };

  if (error) return <p className="text-red-500 text-center">{error}</p>;
  if (!gig) return <p>Loading...</p>;

  return (
    <div className="max-w-3xl mx-auto p-8">
      <h1 className="text-3xl font-bold">{gig.title}</h1>
      <p className="text-gray-700 mt-4">{gig.description}</p>
      <p className="text-gray-900 font-semibold mt-2">Price: â‚¹{gig.price}</p>
      <p className="text-gray-700 mt-2">Delivery Time: {gig.deliveryTime} days</p>

      <button
        onClick={handleOrder}
        className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Place Order
      </button>
    </div>
  );
};

export default GigDetails;
