// File: client/src/pages/CreateGig.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const CreateGig = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    subCategory: '',
    pricingType: 'fixed',
    price: '',
    deliveryTime: '',
    tags: '',
    images: []
  });

  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      images: Array.from(e.target.files)
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const gigData = { ...formData, tags: formData.tags.split(',').map(tag => tag.trim()) };

      const response = await api.post('/gigs', gigData);
      navigate(`/gigs/${response.data._id}`);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create gig');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10 bg-white p-8 border border-gray-300 rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-6 text-center">Create a New Gig</h1>

      {error && <p className="text-red-500 text-center mb-4">{error}</p>}

      <form onSubmit={handleSubmit}>
        <input name="title" type="text" placeholder="Title" value={formData.title} onChange={handleChange} required />
        <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
        <input name="category" type="text" placeholder="Category" value={formData.category} onChange={handleChange} required />
        <input name="subCategory" type="text" placeholder="Subcategory" value={formData.subCategory} onChange={handleChange} />
        <select name="pricingType" value={formData.pricingType} onChange={handleChange}>
          <option value="fixed">Fixed</option>
          <option value="hourly">Hourly</option>
        </select>
        <input name="price" type="number" placeholder="Price" value={formData.price} onChange={handleChange} required />
        <input name="deliveryTime" type="number" placeholder="Delivery time (days)" value={formData.deliveryTime} onChange={handleChange} required />
        <input name="tags" type="text" placeholder="Tags (comma-separated)" value={formData.tags} onChange={handleChange} />
        <input type="file" multiple onChange={handleFileChange} />

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Creating...' : 'Create Gig'}
        </button>
      </form>
    </div>
  );
};

export default CreateGig;
